mapper.select:
  		select * from serialmapper join buylist using(serial)
		join item using(seq) 
		where serial=#{serial}

select * from 
(select rownum rn, tt.* from 
(select * from refundrequest join buylist using(serial) where id=#{id} order by serial desc)tt
) where rn >=${startNum} and rn <= ${endNum}

  		select * from 
			(select rownum rn, tt.* from 
			(select * from refundrequest join buylist using(serial) 
				where id=#{id} and type=#{type} order by serial desc)tt
			) where rn >=${startNum} and rn <= ${endNum}

mapper.insert:
  		begin
	  		insert into refundrequest values(#{serial}
	  										,'주문취소'
	  										,'[고객취소]취소접수'
	  										,'단순변심'
	  										,''
	  										,#{startDate}
	  										,sysdate
	  										,'취소완료');
	  		update buylist set status='취소' where serial=#{serial};
	  		update member set point=point-(
				select sum(price*count)/100 from serialmapper where serial=#{serial})
				where id=#{id};								
  		end;
